import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 4 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 }
    ]
    //component that owns the state should be the one owning it
    //modifying counters eg adding/deleting should be done here
  };
  //local state is invisible to other components
  //props is read-only, only within the same component
  //otherwise, you can take input(prop value) and bring it into the state
  render() {
    return (
      <div>
        {this.state.counters.map(counter => (
          <Counter key={counter.id} value={counter.value} id={counter.id}>
            <h3>Counnter #{counter.id}</h3>
          </Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
